import React, { Component } from 'react';

class Habit extends Component {

    handleIncrement = () =>{
        this.props.onIncrement(this.props.habit);
    }
    handleDecrement = () =>{
        this.props.onDecrement(this.props.habit);
    }
    handlDelete = () => {
      this.props.onDDelete(this.props.habit);
    }

    render() {
      console.log(this.props.habit);
      const habitName =this.props.habit.name;
      const {name,count} = this.props.habit
        return(

            <li className="habit">
              <span className="habit-name">{name}</span>
              <span className="habit-count">{count}</span>
              <button className="habit-button habit-increase" onClick={this.handleIncrement}>
                <i className="fas fa-plus-square"></i>
              </button>
              <button className="habit-button habit-decrease" onClick={this.handleDecrement}>
                <i className="fas fa-minus-square"></i>
              </button>
              {/* <button className="habit-button habit-delete" onClick={() => {this.props.onDelete(this.props.habit)}}> */}
              <button className="habit-button habit-delete" onClick={this.handlDelete}>

                <i className="fas fa-trash"></i>
              </button>
            </li>
          )
    }
}

export default Habit;
