    }
    render() {
        return (
        <li className="habit">
            <span className="habit-name">Reding</span>
            <span className="habit-count">{this.statecount}</span>

            <button
            className="habit-button habit-increase"
            onClick={this.handleIncrement}
            >
            <i className="fas fa-plus-square"></i>
            </button>
        </li>
        );
    }
}

export default SimpleHabit;
