import React,{ Component } from 'react';
import './app.css';
import Habits from './components/habits';
import Navbar from './components/navbar';


class App extends Component{
  state = {
    habits:[
        {id :1, name:'Reading',  count:0},
        {id :2, name:'test',  count:0},
        {id :3, name:'dklsd' ,  count:0},
    ],
};
handleIncrement = habit =>{
   const habits = [...this.state.habits];
   const index = habits.indexOf(habit);
   habits[index].count++;
   this.setState({habits});
}
handleDecrement = habit =>{
    const habits = [...this.state.habits];
    const index = habits.indexOf(habit);
    const count = habits[index].count - 1;
    habits[index].count =  count < 0 ?  0:count ;
    this.setState({habits});
}
handlDelete = (habit) =>{
    const habits = this.state.habits.filter(item => item.id !== habit.id);
    this.setState({habits});
}
  render(){
    return (
      <>
      <Navbar totalCount={this.state.habites.filter(item => item.count > 0).lenth}/>
      <Habits 
        habits ={this.state.habits}
        onIncrement = {this.props.handleIncrement} 
        onDecrement = {this.props.handleDecrement}
        onDelete = {this.props.handlDelete}
      />
      </>
    ) 
  }
}
export default App;
